(58,	'avalara_q3-2023_noAvaTax',	'marketing',	'en_US',	'Automatically calculate VAT in real time',	'Take the effort out of determining tax rates and sell confidently across borders with automated tax management from Avalara AvaTax— including built-in VAT calculation when you sell into or across the EU and UK. Save time and stay compliant when you let Avalara do the heavy lifting.',	'{}',	'pending',	'woocommerce.com',	'2024-01-04 14:15:49',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(59,	'woo-activation-survey-blockers-22AUG23',	'info',	'en_US',	'How can we help you get that first sale?',	'Your feedback is vital. Please take a minute to share your experience of setting up your new store and whether anything is preventing you from making those first few sales. Together, we can make Woo even better!',	'{}',	'unactioned',	'woocommerce.com',	'2024-01-07 12:26:57',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(60,	'woocommerce-usermeta-Sept2023-productvendors',	'update',	'en_US',	'Your store requires a security update',	'<strong>Your shop needs an update to address a vulnerability in WooCommerce.</strong> The fix was released on Sept 15. Please update WooCommerce to the latest version immediately. <a href=\"https://developer.woocommerce.com/2023/09/16/woocommerce-vulnerability-reintroduced-from-7-0-1/\" />Read our developer update</a> for more information.',	'{}',	'pending',	'woocommerce.com',	'2024-01-04 14:15:49',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(61,	'woocommerce-STRIPE-Oct-2023-update-needed',	'update',	'en_US',	'Action required: Security update for WooCommerce Stripe Gateway',	'<strong>Your shop requires a security update to address a vulnerability in the WooCommerce Stripe Gateway</strong>. The fix for this vulnerability was released on October 17. Please update immediately.',	'{}',	'pending',	'woocommerce.com',	'2024-01-04 14:15:49',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(62,	'amazon-mcf-reviews-2023-12-07',	'marketing',	'en_US',	'Enjoying Amazon MCF for WooCommerce?',	'We\'re Never Settle, the developers behind Amazon MCF for WooCommerce, and would be deeply honored to have your review. Reviews help immensely as other users can learn how MCF can solve their needs too! Not happy or need help? Please reach out for support and we’d love to make things right!',	'{}',	'pending',	'woocommerce.com',	'2024-01-04 14:15:49',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(63,	'wc-admin-choosing-a-theme',	'marketing',	'en_US',	'Choosing a theme?',	'Check out the themes that are compatible with WooCommerce and choose one aligned with your brand and business needs.',	'{}',	'unactioned',	'woocommerce-admin',	'2024-01-05 14:38:50',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(64,	'wc-admin-mobile-app',	'info',	'en_US',	'Install Woo mobile app',	'Install the WooCommerce mobile app to manage orders, receive sales notifications, and view key metrics — wherever you are.',	'{}',	'unactioned',	'woocommerce-admin',	'2024-01-07 12:26:54',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(65,	'wc-admin-customizing-product-catalog',	'info',	'en_US',	'How to customize your product catalog',	'You want your product catalog and images to look great and align with your brand. This guide will give you all the tips you need to get your products looking great in your store.',	'{}',	'unactioned',	'woocommerce-admin',	'2024-01-08 17:57:55',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(66,	'wc-admin-onboarding-payments-reminder',	'info',	'en_US',	'Start accepting payments on your store!',	'Take payments with the provider that’s right for you - choose from 100+ payment gateways for WooCommerce.',	'{}',	'unactioned',	'woocommerce-admin',	'2024-01-09 19:22:32',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(67,	'wc-admin-payments-remind-me-later',	'info',	'en_US',	'Save big with WooPayments',	'Save up to $800 in fees by managing transactions with WooPayments. With WooPayments, you can securely accept major cards, Apple Pay, and payments in over 100 currencies.',	'{}',	'unactioned',	'woocommerce-admin',	'2024-01-09 19:22:33',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(68,	'amazon_pay_reactivate_q1-24',	'marketing',	'en_US',	'Special offer: Activate 0% promo rate for Amazon Pay',	'For a limited time, Woo merchants who register with Amazon Pay can enjoy a promotional rate of 0% on all transaction fees for 30 days. Valid for businesses based in Germany, France, Italy, Spain, and the United Kingdom. Terms and conditions apply.',	'{}',	'pending',	'woocommerce.com',	'2024-01-09 19:22:34',	NULL,	0,	'plain',	'',	0,	0,	'info'),
(69,	'wc-admin-woocommerce-payments',	'marketing',	'en_US',	'Try the new way to get paid',	'Securely accept credit and debit cards on your site. Manage transactions without leaving your WordPress dashboard. Only with <strong>WooPayments</strong>.<br><br>By clicking \"Get started\", you agree to our <a href=\"https://wordpress.com/tos/\" target=\"_blank\">Terms of Service</a>',	'{}',	'unactioned',	'woocommerce-admin',	'2024-01-12 17:31:57',	NULL,	0,	'plain',	'',	0,	0,	'info');
